import{_ as t,o as a,c as e,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 24.2-460 Transmission of unvoted ballots.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-460.md","filePath":"vacode/24.2-460.md"}'),i={name:"vacode/24.2-460.md"},r=o('<h1 id="§-24-2-460-transmission-of-unvoted-ballots" tabindex="-1">§ 24.2-460 Transmission of unvoted ballots. <a class="header-anchor" href="#§-24-2-460-transmission-of-unvoted-ballots" aria-label="Permalink to &quot;§ 24.2-460 Transmission of unvoted ballots.&quot;">​</a></h1><p>A. For an election described in § <a href="http://law.lis.virginia.gov/vacode/24.2-454/" target="_blank" rel="noreferrer">24.2-454</a> for which this state has not received a waiver pursuant to § 579 of the Military and Overseas Voter Empowerment Act, 52 U.S.C. § 20302(g)(2), not later than 45 days before the election, the election official in each jurisdiction charged with distributing a ballot and balloting materials shall transmit a ballot and balloting materials to all covered voters who by that date submit a valid military-overseas ballot application.</p><p>B. A covered voter may request that a ballot and balloting materials be sent to the voter as authorized in § <a href="http://law.lis.virginia.gov/vacode/24.2-706/" target="_blank" rel="noreferrer">24.2-706</a>.</p><p>C. If a ballot application from a covered voter arrives after the jurisdiction begins transmitting ballots and balloting materials to voters, the official charged with distributing a ballot and balloting materials shall transmit them to the voter not later than three business days after the application arrives.</p><p>2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>.</p>',5),n=[r];function l(s,d,c,h,v,b){return a(),e("div",null,n)}const p=t(i,[["render",l]]);export{_ as __pageData,p as default};
