import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-504 Persons entitled to have name printed on ballot.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-504.md","filePath":"vacode/24.2-504.md"}'),o={name:"vacode/24.2-504.md"},r=n('<h1 id="§-24-2-504-persons-entitled-to-have-name-printed-on-ballot" tabindex="-1">§ 24.2-504 Persons entitled to have name printed on ballot. <a class="header-anchor" href="#§-24-2-504-persons-entitled-to-have-name-printed-on-ballot" aria-label="Permalink to &quot;§ 24.2-504 Persons entitled to have name printed on ballot.&quot;">​</a></h1><p>Only a person fulfilling all the requirements of a candidate shall have his name printed on the ballot for the election. No person shall have his name printed on the ballot for more than one office at any one election. However, a candidate for federal or statewide office, or a candidate for an office being filled in a special election, may have his name printed on the ballot for two offices at an election.</p><p>Code 1950, § 24-132; 1970, c. 462, § 24.1-167; 1971, Ex. Sess., c. 226; 1973, c. 30; 1975, c. 515; 1976, c. 616; 1977, c. 490; 1978, c. 778; 1980, c. 639; 1982, c. 650; 1984, c. 480; 1987, Sp. Sess., c. 1; 1988, c. 469; 1990, cc. 476, 865; 1991, c. 137; 1993, c. 641; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0513" target="_blank" rel="noreferrer">513</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>.</p>',3),i=[r];function l(c,s,d,f,h,p){return a(),t("div",null,i)}const v=e(o,[["render",l]]);export{m as __pageData,v as default};
