import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-521 Petition required to accompany declaration; number of signatures required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-521.md","filePath":"vacode/24.2-521.md"}'),a={name:"vacode/24.2-521.md"},o=i('<h1 id="§-24-2-521-petition-required-to-accompany-declaration-number-of-signatures-required" tabindex="-1">§ 24.2-521 Petition required to accompany declaration; number of signatures required. <a class="header-anchor" href="#§-24-2-521-petition-required-to-accompany-declaration-number-of-signatures-required" aria-label="Permalink to &quot;§ 24.2-521 Petition required to accompany declaration; number of signatures required.&quot;">​</a></h1><p>A. A candidate for nomination by primary for any office shall be required to file with his declaration of candidacy a petition for his name to be printed on the official primary ballot, on a form prescribed by the State Board, signed by the number of qualified voters specified in this section after January 1 of the year in which the election is held or before or after said date in the case of a March primary, and listing the residence address of each such voter. Each signature on the petition shall have been witnessed by a person who is not a minor or a felon whose voting rights have not been restored and whose affidavit to that effect appears on each page of the petition. Each such person circulating a petition who is not a legal resident of the Commonwealth shall sign a statement on the affidavit that he consents to the jurisdiction of the courts of Virginia in resolving any disputes concerning the circulation of petitions, or signatures contained therein, by that person. The signatures of qualified voters collected by a nonresident petition circulator who fails to sign such statement, or who later fails to appear or produce documents when properly served with a subpoena to do so, shall not be counted towards the minimum number of signatures required pursuant to subsection B.</p><p>Each voter signing the petition may provide on the petition the last four digits of his social security number, if any; however, noncompliance with this requirement shall not be cause to invalidate the voter&#39;s signature on the petition.</p><p>B. The minimum number of signatures of qualified voters required for primary candidate petitions shall be as follows:</p><ol><li><p>For a candidate for the United States Senate, Governor, Lieutenant Governor, or Attorney General, 10,000 signatures, including the signatures of at least 400 qualified voters from each congressional district in the Commonwealth;</p></li><li><p>For a candidate for the United States House of Representatives, 1,000 signatures;</p></li><li><p>For a candidate for the Senate of Virginia, 250 signatures;</p></li><li><p>For a candidate for the House of Delegates or for a constitutional office, 125 signatures;</p></li><li><p>For a candidate for membership on the governing body of any county or city, 125 signatures; or if from an election district not at large containing 1,000 or fewer registered voters, 50 signatures;</p></li><li><p>For a candidate for membership on the governing body of any town that has more than 3,500 registered voters, 125 signatures; or if from a ward or other district not at large, 25 signatures;</p></li><li><p>For a candidate for membership on the governing body of any town that has at least 1,500 but not more than 3,500 registered voters, 50 signatures; or if from a ward or other district not at large, 25 signatures;</p></li><li><p>For a candidate for membership on the governing body of any town that has fewer than 1,500 registered voters, no petition shall be required; and</p></li><li><p>For any other candidate, 50 signatures.</p></li></ol><p>Code 1950, § 24-373; 1952, c. 523; 1970, c. 462, § 24.1-185; 1971, Ex. Sess., cc. 119, 247; 1972, c. 620; 1978, c. 778; 1980, c. 639; 1982, c. 650; 1983, c. 188; 1989, c. 141; 1992, c. 855; 1993, cc. 407, 641; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0152" target="_blank" rel="noreferrer">152</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0246" target="_blank" rel="noreferrer">246</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0232" target="_blank" rel="noreferrer">232</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0252" target="_blank" rel="noreferrer">252</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0215" target="_blank" rel="noreferrer">215</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0355" target="_blank" rel="noreferrer">355</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0501" target="_blank" rel="noreferrer">501</a>.</p>',6),n=[o];function s(l,c,h,d,f,g){return t(),r("div",null,n)}const m=e(a,[["render",s]]);export{u as __pageData,m as default};
