import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-522 When and to whom filings to be made.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-522.md","filePath":"vacode/24.2-522.md"}'),i={name:"vacode/24.2-522.md"},o=n('<h1 id="§-24-2-522-when-and-to-whom-filings-to-be-made" tabindex="-1">§ 24.2-522 When and to whom filings to be made. <a class="header-anchor" href="#§-24-2-522-when-and-to-whom-filings-to-be-made" aria-label="Permalink to &quot;§ 24.2-522 When and to whom filings to be made.&quot;">​</a></h1><p>A. Declarations of candidacy, petitions, and receipts indicating the payment of filing fees shall be filed not earlier than noon of the ninety-second day and not later than 5:00 p.m. of the seventy-fifth day before the primary.</p><p>B. Except as provided in subsection C, candidates for nomination shall file their declarations, petitions, and receipts with the chairman or chairmen of the several committees of the respective parties.</p><p>C. Any candidate for nomination for United States Senator, Governor, Lieutenant Governor, or Attorney General shall file with the State Board (i) his declaration of candidacy, (ii) the petitions for his candidacy, sealed in one or more containers to which is attached a written statement under oath by the candidate giving his name and the number of signatures on the petitions contained in the containers, and (iii) a receipt indicating the payment of his filing fee.</p><p>The State Board shall transmit the material so filed to the state chairman of the party of the candidate within 72 hours and not later than the seventy-fourth day before the primary. The sealed containers containing the petitions for a candidate may be opened only by the state chairman of the party of the candidate.</p><p>Code 1950, §§ 24-370 through 24-372, 24-374; 1960, c. 427; 1970, c. 462, §§ 24.1-184, 24.1-186; 1978, cc. 239, 778, § 24.1-186.1; 1983, c. 461; 1993, c. 641; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1015" target="_blank" rel="noreferrer">1015</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',6),r=[o];function h(s,d,c,l,f,p){return t(),a("div",null,r)}const _=e(i,[["render",h]]);export{g as __pageData,_ as default};
