import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 24.2-527 Chairman or official to furnish State Board and general registrars with names of candidates and certify petition signature requirements met.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-527.md","filePath":"vacode/24.2-527.md"}'),i={name:"vacode/24.2-527.md"},n=r('<h1 id="§-24-2-527-chairman-or-official-to-furnish-state-board-and-general-registrars-with-names-of-candidates-and-certify-petition-signature-requirements-met" tabindex="-1">§ 24.2-527 Chairman or official to furnish State Board and general registrars with names of candidates and certify petition signature requirements met. <a class="header-anchor" href="#§-24-2-527-chairman-or-official-to-furnish-state-board-and-general-registrars-with-names-of-candidates-and-certify-petition-signature-requirements-met" aria-label="Permalink to &quot;§ 24.2-527 Chairman or official to furnish State Board and general registrars with names of candidates and certify petition signature requirements met.&quot;">​</a></h1><p>A. It shall be the duty of the chairman or chairmen of the several committees of the respective parties to furnish the name of any candidate for nomination for any office to be elected by the qualified voters of the Commonwealth at large or of a congressional district or of a General Assembly district to the State Board, and to furnish the name of any candidate for any other office to the State Board and to the general registrars charged with the duty of preparing and printing the primary ballots. In furnishing the name of any such candidate, the chairman shall certify that a review of the filed candidate petitions found the required minimum number of signatures of qualified voters for that office to have been met. The chairman shall also certify the order and date and time of filing for purposes of printing the ballots as prescribed in § <a href="/vacode/24.2-529/">24.2-529</a>, provided that the State Board shall determine the order and date and time of filing for candidates for United States Senator, Governor, Lieutenant Governor, and Attorney General for such purposes. Each chairman shall comply with the provisions of this section not less than 70 days before the primary.</p><p>B. In no case shall the individual who is a candidate for an office be the person who certifies the names of candidates for a primary for that same office. In such case the party shall designate an alternate official to certify the candidates.</p><p>Code 1950, § 24-375; 1970, c. 462, § 24.1-187; 1976, c. 616; 1978, c. 239; 1979, c. 329; 1993, c. 641; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0492" target="_blank" rel="noreferrer">492</a>.</p>',4),o=[n];function s(f,h,c,d,l,g){return a(),t("div",null,o)}const u=e(i,[["render",s]]);export{p as __pageData,u as default};
