import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 24.2-540 Other parties may also nominate; independent candidates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-540.md","filePath":"vacode/24.2-540.md"}'),i={name:"vacode/24.2-540.md"},o=n('<h1 id="§-24-2-540-other-parties-may-also-nominate-independent-candidates" tabindex="-1">§ 24.2-540 Other parties may also nominate; independent candidates. <a class="header-anchor" href="#§-24-2-540-other-parties-may-also-nominate-independent-candidates" aria-label="Permalink to &quot;§ 24.2-540 Other parties may also nominate; independent candidates.&quot;">​</a></h1><p>Any other political party may also nominate and file the required notice of a new candidate pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-539/" target="_blank" rel="noreferrer">24.2-539</a> if the candidate who died, withdrew, or had his nomination set aside was unopposed by that party. A nonparty candidate shall also be permitted to file a notice of candidacy whether or not the candidate who died, withdrew, or had his nomination set aside was opposed by a nonparty or party candidate.</p><p>Any such party or nonparty candidate shall file any statement or petition required of him by Article 1 (§ <a href="http://law.lis.virginia.gov/vacode/24.2-500/" target="_blank" rel="noreferrer">24.2-500</a> et seq.) or Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/24.2-505/" target="_blank" rel="noreferrer">24.2-505</a> et seq.) of this chapter.</p><p>Code 1950, §§ 24-234, 24-235; 1970, c. 462, § 24.1-110; 1976, c. 616; 1980, c. 639; 1984, c. 480; 1990, c. 476; 1992, c. 828; 1993, c. 641.</p>',4),r=[o];function d(s,c,p,h,l,_){return a(),t("div",null,r)}const y=e(i,[["render",d]]);export{f as __pageData,y as default};
