import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-544 Time presidential primaries to be held and completion of duties by officers of election; age qualifications for participation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-544.md","filePath":"vacode/24.2-544.md"}'),r={name:"vacode/24.2-544.md"},o=a('<h1 id="§-24-2-544-time-presidential-primaries-to-be-held-and-completion-of-duties-by-officers-of-election-age-qualifications-for-participation" tabindex="-1">§ 24.2-544 Time presidential primaries to be held and completion of duties by officers of election; age qualifications for participation. <a class="header-anchor" href="#§-24-2-544-time-presidential-primaries-to-be-held-and-completion-of-duties-by-officers-of-election-age-qualifications-for-participation" aria-label="Permalink to &quot;§ 24.2-544 Time presidential primaries to be held and completion of duties by officers of election; age qualifications for participation.&quot;">​</a></h1><p>A. Primaries for the nomination of candidates for the office of President of the United States to be voted on at the November 2012 general election and the November general election in each presidential election year thereafter shall be held on the first Tuesday in March preceding the November general election.</p><p>B. The provisions of this title shall apply to the conduct of presidential election year primaries including the time limits applicable to notices and candidate filing deadlines and the closing of registration records before the primary. The State Board shall provide a schedule for the notices and filing deadlines by the August 1 prior to the March primary including a campaign finance disclosure report filing schedule adjusted to reflect the differences between the June date for other primaries and the March date for the presidential primary and primaries for the nomination of candidates for offices to be voted on at the general election date in May.</p><p>C. Notwithstanding any other provision of law to the contrary, any officer of election who serves at any election held on the first Tuesday in March shall be required to complete his official duties relating to that election whether or not he has been reappointed to serve for the ensuing year.</p><p>D. Notwithstanding any other provision of law to the contrary, any person who is otherwise qualified and will be 18 years of age on or before the day of the next November general election shall be permitted to register in advance of and also vote in any presidential primary and any other primary held on the same day.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0972" target="_blank" rel="noreferrer">972</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0205" target="_blank" rel="noreferrer">205</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0570" target="_blank" rel="noreferrer">570</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0584" target="_blank" rel="noreferrer">584</a>.</p>',6),n=[o];function l(s,c,f,d,h,p){return i(),t("div",null,n)}const b=e(r,[["render",l]]);export{m as __pageData,b as default};
