import{_ as e,o,c as t,O as l}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-603 Hours polls to be open; closing the polls.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-603.md","filePath":"vacode/24.2-603.md"}'),a={name:"vacode/24.2-603.md"},n=l('<h1 id="§-24-2-603-hours-polls-to-be-open-closing-the-polls" tabindex="-1">§ 24.2-603 Hours polls to be open; closing the polls. <a class="header-anchor" href="#§-24-2-603-hours-polls-to-be-open-closing-the-polls" aria-label="Permalink to &quot;§ 24.2-603 Hours polls to be open; closing the polls.&quot;">​</a></h1><p>At all elections, the polls shall be open at each polling place at 6:00 a.m. on the day of the election and closed at 7:00 p.m. on the same day except as provided for central absentee voter precincts pursuant to subsection G of § <a href="/vacode/24.2-712/">24.2-712</a>.</p><p>At 6:45 p.m. an officer of election shall announce that the polls will close in fifteen minutes. The officers of election shall list the names of all qualified voters in line before the polling place at 7:00 p.m. and permit those voters and no others to vote after 7:00 p.m.</p><p>Code 1950, §§ 24-182, 24-184; 1950, p. 462; 1958, c. 160; 1962, c. 536; 1966, c. 116; 1970, c. 462, §§ 24.1-98, 24.1-99; 1981, c. 425; 1993, c. 641; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0423" target="_blank" rel="noreferrer">423</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0471" target="_blank" rel="noreferrer">471</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0522" target="_blank" rel="noreferrer">522</a>.</p>',4),s=[n];function r(i,c,p,h,f,_){return o(),t("div",null,s)}const u=e(a,[["render",r]]);export{g as __pageData,u as default};
