import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-622 Unofficial sample ballots.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-622.md","filePath":"vacode/24.2-622.md"}'),o={name:"vacode/24.2-622.md"},l=r('<h1 id="§-24-2-622-unofficial-sample-ballots" tabindex="-1">§ 24.2-622 Unofficial sample ballots. <a class="header-anchor" href="#§-24-2-622-unofficial-sample-ballots" aria-label="Permalink to &quot;§ 24.2-622 Unofficial sample ballots.&quot;">​</a></h1><p>Sample ballots not authorized by electoral boards and provided by electoral boards or general registrars to precincts pursuant to § <a href="/vacode/24.2-641/">24.2-641</a> are permitted to be printed and circulated, which includes publication in newspapers or on the Internet.</p><p>Such sample ballots shall not be printed on white paper and shall include on their face the words &quot;sample ballot&quot; in a font size no smaller than 24 point.</p><p>All sample ballots, excepting those official sample ballots authorized by electoral boards and provided by electoral boards or general registrars to precincts pursuant to § <a href="/vacode/24.2-641/">24.2-641</a>, are advertisements for purposes of Chapter 9.5 (§ <a href="/vacode/24.2-955/">24.2-955</a> et seq.). Voters may take sample ballots into the voting booth or enclosure, but shall not give, tender, or exhibit such sample ballot to any person, other than an assistant designated under § <a href="/vacode/24.2-649/">24.2-649</a>, while inside the polling place or within the prohibited area designated by § <a href="/vacode/24.2-604/">24.2-604</a>.</p><p>Code 1950, § 24-240; 1970, c. 462, § 24.1-122; 1974, c. 428; 1979, c. 265; 1993, c. 641; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1015" target="_blank" rel="noreferrer">1015</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0370" target="_blank" rel="noreferrer">370</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>.</p>',5),i=[l];function n(s,c,p,d,h,f){return a(),t("div",null,i)}const _=e(o,[["render",n]]);export{g as __pageData,_ as default};
