import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 24.2-625.2 Wireless communications at polling places.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-625.2.md","filePath":"vacode/24.2-625.2.md"}'),a={name:"vacode/24.2-625.2.md"},n=o('<h1 id="§-24-2-625-2-wireless-communications-at-polling-places" tabindex="-1">§ 24.2-625.2 Wireless communications at polling places. <a class="header-anchor" href="#§-24-2-625-2-wireless-communications-at-polling-places" aria-label="Permalink to &quot;§ 24.2-625.2 Wireless communications at polling places.&quot;">​</a></h1><p>There shall be no wireless communications on election day, while the polls are open, between or among voting machines within the polling place or between any voting machine within the polling place and any equipment outside the polling place. For purposes of this section, the term wireless communication shall mean the ability to transfer information via electromagnetic waves without the use of electrical conductors.</p><p>The provisions of this section shall not apply to voting machines purchased by any locality before July 1, 2007.</p><p>The provisions of this section shall not be construed to prohibit the operation of electronic pollbook devices at polling places on election day.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0393" target="_blank" rel="noreferrer">393</a>.</p>',5),l=[n];function r(s,c,p,h,g,_){return i(),t("div",null,l)}const d=e(a,[["render",r]]);export{f as __pageData,d as default};
