import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 24.2-636 Instruction as to use of equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-636.md","filePath":"vacode/24.2-636.md"}'),r={name:"vacode/24.2-636.md"},a=n('<h1 id="§-24-2-636-instruction-as-to-use-of-equipment" tabindex="-1">§ 24.2-636 Instruction as to use of equipment. <a class="header-anchor" href="#§-24-2-636-instruction-as-to-use-of-equipment" aria-label="Permalink to &quot;§ 24.2-636 Instruction as to use of equipment.&quot;">​</a></h1><p>No fewer than three nor more than 30 days before each election, the electoral board or general registrar shall instruct, or cause to be instructed, on the use of the equipment and his duties in connection therewith, each officer of election appointed to serve in the election who has not previously been so instructed. The board or the general registrar shall not permit any person to serve as an officer who is not fully trained to conduct an election properly with the equipment. This section shall not be construed to prevent the appointment of a person as an officer of election to fill a vacancy in an emergency.</p><p>Code 1950, § 24-303; 1970, c. 462, § 24.2-213; 1972, c. 620; 1985, c. 458; 1993, c. 641; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0187" target="_blank" rel="noreferrer">187</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0492" target="_blank" rel="noreferrer">492</a>.</p>',3),i=[a];function s(c,l,h,p,f,u){return t(),o("div",null,i)}const g=e(r,[["render",s]]);export{d as __pageData,g as default};
