import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 24.2-653.3 Voters assigned to split precinct; provisional ballots.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-653.3.md","filePath":"vacode/24.2-653.3.md"}'),a={name:"vacode/24.2-653.3.md"},s=i('<h1 id="§-24-2-653-3-voters-assigned-to-split-precinct-provisional-ballots" tabindex="-1">§ 24.2-653.3 Voters assigned to split precinct; provisional ballots. <a class="header-anchor" href="#§-24-2-653-3-voters-assigned-to-split-precinct-provisional-ballots" aria-label="Permalink to &quot;§ 24.2-653.3 Voters assigned to split precinct; provisional ballots.&quot;">​</a></h1><p>A. Any voter who is assigned to a precinct that is split between two or more election districts and believes he was given a ballot for the district of which he is not a qualified voter may request, prior to casting the ballot, and shall be permitted to cast a provisional ballot for the district of which he believes he is a qualified voter and for the district in which the pollbook indicates he is registered. The provisional ballots shall be cast in accordance with the provisions of § <a href="/vacode/24.2-653/">24.2-653</a>, except that the voter shall be given a printed ballot for the district of which he believes he is a qualified voter and for the district in which the pollbook indicates he is registered, and an envelope for each ballot, which shall be labeled with the corresponding district number. After marking each printed ballot, the voter shall seal each ballot in its corresponding envelope, and the ballot envelopes shall then be sealed in the green envelope provided for in § <a href="/vacode/24.2-653/">24.2-653</a>.</p><p>B. At the meeting of the electoral board to determine the validity of all provisional ballots offered in the election, the electoral board shall verify in which district a voter who voted provisionally pursuant to this section is a qualified voter, and the provisional ballot cast by the voter for that district shall be counted. The electoral board shall process the ballot in accordance with the provisions of § <a href="/vacode/24.2-653.01/">24.2-653.01</a> and the instructions of the State Board.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0920" target="_blank" rel="noreferrer">920</a>, § 24.2-653.2.</p>',4),r=[s];function l(n,h,c,d,p,v){return t(),o("div",null,r)}const _=e(a,[["render",l]]);export{f as __pageData,_ as default};
