import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 24.2-703 Application for absentee ballots for multiple elections for uniformed and overseas voters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-703.md","filePath":"vacode/24.2-703.md"}'),i={name:"vacode/24.2-703.md"},o=r('<h1 id="§-24-2-703-application-for-absentee-ballots-for-multiple-elections-for-uniformed-and-overseas-voters" tabindex="-1">§ 24.2-703 Application for absentee ballots for multiple elections for uniformed and overseas voters. <a class="header-anchor" href="#§-24-2-703-application-for-absentee-ballots-for-multiple-elections-for-uniformed-and-overseas-voters" aria-label="Permalink to &quot;§ 24.2-703 Application for absentee ballots for multiple elections for uniformed and overseas voters.&quot;">​</a></h1><p>Any person who is eligible for a military-overseas ballot as defined in § <a href="/vacode/24.2-452/">24.2-452</a> may file a single application to receive ballots for all elections in which he is eligible to vote absentee. The application shall be on a federal postcard application. An application from any person who is already registered or who is eligible for late registration under § <a href="/vacode/24.2-419/">24.2-419</a> that is received by the general registrar no later than 5:00 p.m. on the eleventh day prior to the election shall be considered a standing request for absentee ballots through December 31 of the year following the calendar year of the date of the application or another shorter period the voter specifies. In the event that a second or subsequent federal postcard application is received from a voter, any previous applications shall be superseded and the duration of the most recently received application shall apply.</p><p>The general registrar shall retain the application and process the applicant&#39;s request for an absentee ballot for each election in accordance with procedures established by the State Board. The applicant shall specify by party designation the primary ballots he is requesting.</p><p>If an official reply to the application or an absentee ballot sent to the applicant is returned as undeliverable, no other ballots shall be sent. No ballot shall be sent to the applicant, and no voted ballot received from the applicant shall be valid, (i) for any election held after the voter has notified the registrar that the voter no longer wishes to be registered or (ii) after the registrar has received notification that the voter has registered to vote in another state.</p><p>1991, c. 603, § 24.1-228.1:1; 1993, c. 641; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0438" target="_blank" rel="noreferrer">438</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0427" target="_blank" rel="noreferrer">427</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0458" target="_blank" rel="noreferrer">458</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0289" target="_blank" rel="noreferrer">289</a>.</p>',5),l=[o];function n(s,p,c,h,f,d){return a(),t("div",null,l)}const v=e(i,[["render",n]]);export{b as __pageData,v as default};
