import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-800 Recounts in all elections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-800.md","filePath":"vacode/24.2-800.md"}'),n={name:"vacode/24.2-800.md"},r=o('<h1 id="§-24-2-800-recounts-in-all-elections" tabindex="-1">§ 24.2-800 Recounts in all elections. <a class="header-anchor" href="#§-24-2-800-recounts-in-all-elections" aria-label="Permalink to &quot;§ 24.2-800 Recounts in all elections.&quot;">​</a></h1><p>A. The provisions of this article apply to all elections held in the Commonwealth.</p><p>B. When there is between any candidate apparently nominated or elected and any candidate apparently defeated a difference of not more than one percent of the total vote cast for the two such candidates as determined by the State Board or the electoral board, the defeated candidate may appeal from the determination of the State Board or the electoral board for a recount of the vote as set forth in this article. When there is between any write-in candidate apparently nominated or elected and any candidate apparently defeated, or between any candidate apparently nominated or elected and any write-in candidate apparently defeated, a difference of not more than five percent of the total vote cast for the two such candidates as determined by the State Board or the electoral board, the defeated candidate may appeal from the determination of the State Board or the electoral board for a recount of the vote as set forth in this article. In an election of electors for the President and Vice President of the United States, the presidential candidate shall represent the vice presidential candidate and slate of electors and be the party to the recount for purposes of this article.</p><p>C. When there is between the vote for a question and the vote against a question a difference of not more than 50 votes or one percent of the total vote cast for and against the question as determined by the State Board or the electoral board, whichever is greater, 50 or more voters qualified to vote on the question, by signing and filing their petition, may appeal from the determination of the State Board or the electoral board for a recount of the vote as set forth in this article.</p><p>1979, c. 293, § 24.1-249; 1981, c. 570; 1993, c. 641; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0386" target="_blank" rel="noreferrer">386</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0382" target="_blank" rel="noreferrer">382</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0886" target="_blank" rel="noreferrer">886</a>.</p>',5),i=[r];function d(c,l,s,h,f,p){return t(),a("div",null,i)}const u=e(n,[["render",d]]);export{m as __pageData,u as default};
