import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-808 Time of filing and service of complaint; enlargement or amendment of complaint.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-808.md","filePath":"vacode/24.2-808.md"}'),n={name:"vacode/24.2-808.md"},i=o('<h1 id="§-24-2-808-time-of-filing-and-service-of-complaint-enlargement-or-amendment-of-complaint" tabindex="-1">§ 24.2-808 Time of filing and service of complaint; enlargement or amendment of complaint. <a class="header-anchor" href="#§-24-2-808-time-of-filing-and-service-of-complaint-enlargement-or-amendment-of-complaint" aria-label="Permalink to &quot;§ 24.2-808 Time of filing and service of complaint; enlargement or amendment of complaint.&quot;">​</a></h1><p>The contestant shall file his complaint in the clerk&#39;s office of the circuit court within 30 days following the date of the election in the case of a general election, and within 10 days following the date of the election in case of a primary election or special election held on a date other than that of a general election. A copy of the complaint shall be served by the contestant as provided under § <a href="http://law.lis.virginia.gov/vacode/8.01-296/" target="_blank" rel="noreferrer">8.01-296</a> on each contestee; otherwise the complaint shall not be valid. For a contest conducted pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-806/" target="_blank" rel="noreferrer">24.2-806</a>, the copy of the complaint shall be served by the contestant on each contestee within 30 days following the date of the election in the case of a general election and within 10 days following the date of the election in the case of a primary or special election held on a date other than that of a general election.</p><p>No enlargement or amendment of the complaint, except as to form, shall be permitted save by leave of court as provided in Rule 1:8 of the Rules of Supreme Court of Virginia.</p><p>Code 1950, § 24-434; 1952, c. 489; 1970, c. 462, § 24.1-241; 1981, c. 570; 1993, c. 641; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0014" target="_blank" rel="noreferrer">14</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>.</p>',4),r=[i];function l(c,f,s,h,d,m){return t(),a("div",null,r)}const _=e(n,[["render",l]]);export{g as __pageData,_ as default};
