import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 24.2-946.1 Standards and requirements for electronic preparation and transmittal of campaign finance disclosure reports; database.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-946.1.md","filePath":"vacode/24.2-946.1.md"}'),i={name:"vacode/24.2-946.1.md"},n=t('<h1 id="§-24-2-946-1-standards-and-requirements-for-electronic-preparation-and-transmittal-of-campaign-finance-disclosure-reports-database" tabindex="-1">§ 24.2-946.1 Standards and requirements for electronic preparation and transmittal of campaign finance disclosure reports; database. <a class="header-anchor" href="#§-24-2-946-1-standards-and-requirements-for-electronic-preparation-and-transmittal-of-campaign-finance-disclosure-reports-database" aria-label="Permalink to &quot;§ 24.2-946.1 Standards and requirements for electronic preparation and transmittal of campaign finance disclosure reports; database.&quot;">​</a></h1><p>A. The State Board shall review or cause to be developed and shall approve standards for the preparation, production, and transmittal by computer or electronic means of campaign finance reports required by this chapter. The State Board may prescribe the method of execution and certification of and the procedures for receiving electronically filed campaign finance reports required by this chapter in the office of the State Board or any local electoral board. The State Board may provide campaign finance report-creation software to filers without charge or at a reasonable cost.</p><p>B. The State Board shall accept any campaign finance report filed by candidates for the General Assembly and statewide office by computer or electronic means in accordance with the standards approved by the Board and using software meeting standards approved by it. This information shall be made available to the public promptly by the Board through the Internet.</p><p>C. The State Board of Elections shall develop and implement a centralized system to accept reports from candidates for local and constitutional offices. Such reports shall be filed in accordance with, and using software that meets, standards approved by the State Board. The State Board shall promptly notify the general registrar of the locality in which a candidate resides and make the information contained in the report available to the general registrar. In the case of a former candidate who is no longer seeking election but has not yet filed a final report as required by § <a href="/vacode/24.2-948.4/">24.2-948.4</a>, the State Board shall promptly notify the general registrar of the locality in which he sought office and make the information contained in the report available to such general registrar.</p><p>D. The State Board shall enter or cause to be entered into a campaign finance database, available to the public through the Internet, the information from required campaign finance reports filed by computer, electronic, or other means by candidates for the General Assembly and statewide office.</p><p>E. Other campaign finance reports required by this chapter to be filed by a committee with the State Board or a general registrar, or both, may be filed electronically on terms agreed to by the committee and the Board.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0687" target="_blank" rel="noreferrer">687</a>, § 24.2-914.1; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0242" target="_blank" rel="noreferrer">242</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0286" target="_blank" rel="noreferrer">286</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0683" target="_blank" rel="noreferrer">683</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0769" target="_blank" rel="noreferrer">769</a>.</p>',7),o=[n];function l(c,s,d,p,f,h){return a(),r("div",null,o)}const m=e(i,[["render",l]]);export{b as __pageData,m as default};
