import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-946 Summary of election laws; forms; instructions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-946.md","filePath":"vacode/24.2-946.md"}'),o={name:"vacode/24.2-946.md"},a=i('<h1 id="§-24-2-946-summary-of-election-laws-forms-instructions" tabindex="-1">§ 24.2-946 Summary of election laws; forms; instructions. <a class="header-anchor" href="#§-24-2-946-summary-of-election-laws-forms-instructions" aria-label="Permalink to &quot;§ 24.2-946 Summary of election laws; forms; instructions.&quot;">​</a></h1><p>A. The State Board shall summarize the provisions of the election laws relating to the Campaign Finance Disclosure Act of 2006 and provide for distribution of this summary and prescribed forms to each candidate, person, or committee on request or upon their first filing with the State Board pursuant to this chapter, whichever occurs first.</p><p>B. The Board shall designate the forms required for complying with this chapter which shall be the only such forms used in complying with the provisions of this chapter.</p><p>C. The Board shall provide, with the summary required by this section, instructions for persons filing reports pursuant to this chapter to assist them in completing the reports. The instructions shall include directions for the reporting of filing fees for any party nomination method. The instructions shall set out the requirements for retaining records and materials for implementing the review provisions of § <a href="https://law.lis.virginia.gov/vacode/24.2-948.5/" target="_blank" rel="noreferrer">24.2-948.5</a>.</p><p>D. The Board shall provide instructions for candidates who seek election for successive terms in the same office for the filing of reports within each appropriate election cycle for the office and for the aggregation of contributions within each election cycle.</p><p>E. The Board shall provide, with the summary required by this section, to each candidate, person, or committee on request or upon their first filing with the State Board pursuant to this chapter, whichever occurs first, a copy of a written explanation prepared by the Attorney General of the provisions of the Act that prohibit the personal use of campaign funds. The explanation shall cover the provisions that prohibit the personal use of campaign funds and shall delineate the differences between prohibited personal uses of campaign funds and permitted uses of the funds.</p><p>1970, c. 462, § 24.1-252; 1975, c. 515; 1983, c. 119; 1992, c. 447; 1993, c. 641, § 24.2-903; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0268" target="_blank" rel="noreferrer">268</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',7),s=[a];function n(h,c,l,f,p,d){return t(),r("div",null,s)}const g=e(o,[["render",n]]);export{m as __pageData,g as default};
