import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 24.2-947.5 With whom candidates file reports; electronic filing requirement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-947.5.md","filePath":"vacode/24.2-947.5.md"}'),a={name:"vacode/24.2-947.5.md"},n=t('<h1 id="§-24-2-947-5-with-whom-candidates-file-reports-electronic-filing-requirement" tabindex="-1">§ 24.2-947.5 With whom candidates file reports; electronic filing requirement. <a class="header-anchor" href="#§-24-2-947-5-with-whom-candidates-file-reports-electronic-filing-requirement" aria-label="Permalink to &quot;§ 24.2-947.5 With whom candidates file reports; electronic filing requirement.&quot;">​</a></h1><p>A. Candidates for statewide office and for the General Assembly shall file the reports required by this article by computer or electronic means in accordance with the standards approved by the State Board.</p><p>B. Candidates for local or constitutional office in any locality shall file reports required by this article with the State Board by computer or electronic means in accordance with the standards approved by the State Board and shall not be required to file reports with the general registrar of the locality in which the candidate resides.</p><p>C. Except as provided in § <a href="/vacode/24.2-948.1/">24.2-948.1</a>, candidates for any other office who file reports in nonelectronic format shall file with the general registrar of the locality in which the candidate resides.</p><p>D. Notwithstanding the provisions of subsection C, a former candidate who is no longer seeking election but has not yet filed a final report as required by § <a href="/vacode/24.2-948.4/">24.2-948.4</a> and who files reports in nonelectronic format shall file with the general registrar of the locality in which he sought office.</p><p>E. Any report that may be filed with the State Board by mail shall be (i) received by the State Board by the deadline for filing the report or (ii) transmitted to the State Board by telephonic transmission to a facsimile device by the deadline for filing the report with an original copy of the report mailed to the State Board and postmarked by the deadline for filing the report.</p><p>1970, c. 462, §§ 24.1-253, 24.1-254; 1971, Ex. Sess., c. 222; 1972, cc. 620, 622; 1975, c. 515; 1978, c. 778; 1983, c. 119; 1984, c. 480; 1986, c. 558, § 24.1-257.2; 1988, c. 734; 1991, c. 709; 1993, c. 641, §§ 24.2-904, 24.2-915; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0304" target="_blank" rel="noreferrer">304</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0468" target="_blank" rel="noreferrer">468</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0248" target="_blank" rel="noreferrer">248</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0441" target="_blank" rel="noreferrer">441</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0480" target="_blank" rel="noreferrer">480</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0286" target="_blank" rel="noreferrer">286</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0683" target="_blank" rel="noreferrer">683</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0769" target="_blank" rel="noreferrer">769</a>.</p>',7),l=[n];function o(c,h,g,f,s,p){return r(),i("div",null,l)}const v=e(a,[["render",o]]);export{b as __pageData,v as default};
