import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-947.8 Filing requirements for special elections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-947.8.md","filePath":"vacode/24.2-947.8.md"}'),i={name:"vacode/24.2-947.8.md"},l=r('<h1 id="§-24-2-947-8-filing-requirements-for-special-elections" tabindex="-1">§ 24.2-947.8 Filing requirements for special elections. <a class="header-anchor" href="#§-24-2-947-8-filing-requirements-for-special-elections" aria-label="Permalink to &quot;§ 24.2-947.8 Filing requirements for special elections.&quot;">​</a></h1><p>A. Candidates for nomination or election to an office to be filled by a special election held on a regular election date shall file the prescribed reports of contributions and expenditures which apply to regularly scheduled elections for that office.</p><p>B. In the case of a special election held on a date other than a regularly scheduled general election, the candidate shall file as follows:</p><ol><li><p>A report not later than the eighth day before the special election date complete through the twelfth day before that date;</p></li><li><p>A postelection report no later than the thirtieth day after the election and prior to taking office; and</p></li><li><p>A postelection report not later than January 15 and July 15 each year until a final report is filed.</p></li></ol><p>C. Any candidate, who has been subject to the election year filing schedule set out in subdivisions B 1 through B 3 and who has not filed a final report, shall file reports in any subsequent election year for the same office in accordance with the election year filing schedule set out in § <a href="http://law.lis.virginia.gov/vacode/24.2-947.6/" target="_blank" rel="noreferrer">24.2-947.6</a> or <a href="http://law.lis.virginia.gov/vacode/24.2-947.7/" target="_blank" rel="noreferrer">24.2-947.7</a> as appropriate for that office.</p><p>D. Any candidate shall also file any report of certain large contributions required by § <a href="http://law.lis.virginia.gov/vacode/24.2-947.9/" target="_blank" rel="noreferrer">24.2-947.9</a>, if applicable.</p><p>1986, c. 558, § 24.1-257.2; 1988, c. 734; 1991, c. 709; 1993, cc. 639, 641, § 24.2-918; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0696" target="_blank" rel="noreferrer">696</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>.</p>',7),o=[l];function n(c,s,h,f,p,d){return t(),a("div",null,o)}const _=e(i,[["render",n]]);export{u as __pageData,_ as default};
