import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-948.2 Reports as condition to qualification for office.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-948.2.md","filePath":"vacode/24.2-948.2.md"}'),t={name:"vacode/24.2-948.2.md"},o=a('<h1 id="§-24-2-948-2-reports-as-condition-to-qualification-for-office" tabindex="-1">§ 24.2-948.2 Reports as condition to qualification for office. <a class="header-anchor" href="#§-24-2-948-2-reports-as-condition-to-qualification-for-office" aria-label="Permalink to &quot;§ 24.2-948.2 Reports as condition to qualification for office.&quot;">​</a></h1><p>A. No person shall be permitted to qualify for any office, enter upon the duties thereof, or receive any salary or emoluments therefrom until he has filed the campaign finance reports required in subdivisions A 3 through A 9 of § <a href="http://law.lis.virginia.gov/vacode/24.2-947.6/" target="_blank" rel="noreferrer">24.2-947.6</a>; subdivisions A 3 through A 6 of § <a href="http://law.lis.virginia.gov/vacode/24.2-947.7/" target="_blank" rel="noreferrer">24.2-947.7</a>; and subdivisions B 1 and B 2 of § <a href="http://law.lis.virginia.gov/vacode/24.2-947.8/" target="_blank" rel="noreferrer">24.2-947.8</a>, as applicable; and a final report if required by subsection C of § <a href="http://law.lis.virginia.gov/vacode/24.2-948.1/" target="_blank" rel="noreferrer">24.2-948.1</a>; and has responded to and complied with any notice that additional information is required to complete a report in compliance with § <a href="http://law.lis.virginia.gov/vacode/24.2-953.3/" target="_blank" rel="noreferrer">24.2-953.3</a>. No person shall be permitted to qualify for any office, enter upon the duties thereof, or receive any salary or emoluments therefrom until he has paid any civil penalty and returned any contribution required to be returned pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-953.5/" target="_blank" rel="noreferrer">24.2-953.5</a>. No officer authorized by the laws of this Commonwealth to issue certificates of election shall issue one to any person determined to be elected to any such office, until copies of the reports cited above have been filed as required in this article.</p><p>B. Notwithstanding the requirements of subsection A, a person who is elected to fill a vacancy at a special election held on a general election day may qualify for the office and be issued a certificate of election in advance of filing the postelection report required to be filed under subdivision A 9 of § <a href="http://law.lis.virginia.gov/vacode/24.2-947.6/" target="_blank" rel="noreferrer">24.2-947.6</a> in the case of a November election, or under subdivision A 6 of § <a href="http://law.lis.virginia.gov/vacode/24.2-947.7/" target="_blank" rel="noreferrer">24.2-947.7</a> in the case of a May election, upon the filing of a postelection report complete through the election day.</p><p>1970, c. 462, § 24.1-260; 1972, c. 620; 1982, c. 650; 1986, c. 558; 1988, c. 734; 1993, c. 641, § 24.2-922; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0785" target="_blank" rel="noreferrer">785</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0120" target="_blank" rel="noreferrer">120</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0771" target="_blank" rel="noreferrer">771</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>.</p>',4),n=[o];function l(f,c,s,h,d,p){return r(),i("div",null,n)}const v=e(t,[["render",l]]);export{u as __pageData,v as default};
