import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-948.3 Compliance with reporting requirements of campaign finance disclosure act as requirement of candidacy for certain offices.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-948.3.md","filePath":"vacode/24.2-948.3.md"}'),r={name:"vacode/24.2-948.3.md"},n=t('<h1 id="§-24-2-948-3-compliance-with-reporting-requirements-of-campaign-finance-disclosure-act-as-requirement-of-candidacy-for-certain-offices" tabindex="-1">§ 24.2-948.3 Compliance with reporting requirements of campaign finance disclosure act as requirement of candidacy for certain offices. <a class="header-anchor" href="#§-24-2-948-3-compliance-with-reporting-requirements-of-campaign-finance-disclosure-act-as-requirement-of-candidacy-for-certain-offices" aria-label="Permalink to &quot;§ 24.2-948.3 Compliance with reporting requirements of campaign finance disclosure act as requirement of candidacy for certain offices.&quot;">​</a></h1><p>A. It shall be a requirement of candidacy in any election for statewide office or the General Assembly that the candidate shall have filed the disclosure reports required by this chapter for any election in which he participated as a candidate for any such office and which was held within the five years preceding the date of the election in which he seeks to be a candidate. For the purposes of this section, the candidate shall be presumed to have complied with the candidate disclosure reporting requirements unless (i) the State Board or general registrar, whichever is appropriate, has notified the candidate, at least 60 days prior to the applicable deadline for him to file his written statement of qualification set out in § <a href="http://law.lis.virginia.gov/vacode/24.2-503/" target="_blank" rel="noreferrer">24.2-503</a>, that he has failed to file a required report or reports and (ii) the candidate fails to file the specified report or reports by the applicable deadline for filing his written statement of qualification.</p><p>B. The authority of the State Board to grant an extension of the deadline established in § <a href="http://law.lis.virginia.gov/vacode/24.2-503/" target="_blank" rel="noreferrer">24.2-503</a> shall include the authority to grant such extension with respect to the requirements of this section.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0752" target="_blank" rel="noreferrer">752</a>, § 24.2-503.1; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>.</p>',4),o=[n];function c(s,l,f,h,d,p){return a(),i("div",null,o)}const m=e(r,[["render",c]]);export{u as __pageData,m as default};
