import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 24.2-949.3 Use of candidate's name in name of political committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-949.3.md","filePath":"vacode/24.2-949.3.md"}`),o={name:"vacode/24.2-949.3.md"},n=a('<h1 id="§-24-2-949-3-use-of-candidate-s-name-in-name-of-political-committee" tabindex="-1">§ 24.2-949.3 Use of candidate&#39;s name in name of political committee. <a class="header-anchor" href="#§-24-2-949-3-use-of-candidate-s-name-in-name-of-political-committee" aria-label="Permalink to &quot;§ 24.2-949.3 Use of candidate&#39;s name in name of political committee.&quot;">​</a></h1><p>A. No political action committee required to file a statement of organization pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-949.2/" target="_blank" rel="noreferrer">24.2-949.2</a> shall include in any part of its name the name of a candidate unless the political action committee either (i) has obtained, prior to filing, the written authorization of the candidate to use the candidate&#39;s name as part of the name of the political action committee or (ii) has mailed by certified mail, 21 or more days prior to filing, written notice to the candidate of its intent to use his name as part of the name of the political action committee.</p><p>B. Any political action committee which intends to use the name of a candidate as part of the name of the political action committee shall file with the statement of organization required by § <a href="http://law.lis.virginia.gov/vacode/24.2-949.2/" target="_blank" rel="noreferrer">24.2-949.2</a> either (i) a copy of the written authorization of the candidate consenting to the use of his name or (ii) a copy of its notice to the candidate and evidence of its timely mailing. If two candidates seeking the same office have the same surname, the political action committee shall include the first name, or other initial or nickname, and the last name of the candidate, in the name of the political action committee so as to identify which candidate is associated with the political action committee; and either the written authorization of the identified candidate or written notice to the identified candidate shall be required by this section.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0601" target="_blank" rel="noreferrer">601</a>, § 24.2-908.1; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',4),r=[n];function c(l,s,h,d,m,f){return t(),i("div",null,r)}const g=e(o,[["render",c]]);export{_ as __pageData,g as default};
