import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-949.8 With whom political action committees file reports; electronic filing requirement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-949.8.md","filePath":"vacode/24.2-949.8.md"}'),a={name:"vacode/24.2-949.8.md"},o=r('<h1 id="§-24-2-949-8-with-whom-political-action-committees-file-reports-electronic-filing-requirement" tabindex="-1">§ 24.2-949.8 With whom political action committees file reports; electronic filing requirement. <a class="header-anchor" href="#§-24-2-949-8-with-whom-political-action-committees-file-reports-electronic-filing-requirement" aria-label="Permalink to &quot;§ 24.2-949.8 With whom political action committees file reports; electronic filing requirement.&quot;">​</a></h1><p>A. Political action committees required to file reports by this article shall file all statements and campaign finance reports with the State Board.</p><p>B. A political action committee that is required by this chapter to file reports with the State Board, and that accepts contributions or makes expenditures in excess of $10,000 in any calendar year, or that accepted contributions or made expenditures in excess of $10,000 in the previous calendar year, shall file its reports with the State Board by computer or electronic means in accordance with the standards approved by the State Board until such time as the political action committee files a final report. Any political action committee that has been filing electronically, but does not anticipate accepting contributions or making expenditures in excess of $10,000 in the upcoming calendar year, may sign a waiver, on a form prescribed by the State Board, to exempt the committee from the electronic filing requirement for the calendar year. Such waiver form shall be submitted and received no later than the date the first report is due covering activity for that calendar year.</p><p>C. For political action committees that are not subject to the provisions of subsection B, any report required to be filed with the State Board shall be deemed to be filed by the deadline for the report if it is mailed and postmarked not later than the deadline for filing the report.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0687" target="_blank" rel="noreferrer">687</a>, § 24.2-914.1; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0242" target="_blank" rel="noreferrer">242</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',5),n=[o];function l(c,s,h,p,f,g){return t(),i("div",null,n)}const u=e(a,[["render",l]]);export{m as __pageData,u as default};
