import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-950.7 Large dollar reporting requirement for political party committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-950.7.md","filePath":"vacode/24.2-950.7.md"}'),o={name:"vacode/24.2-950.7.md"},a=i('<h1 id="§-24-2-950-7-large-dollar-reporting-requirement-for-political-party-committees" tabindex="-1">§ 24.2-950.7 Large dollar reporting requirement for political party committees. <a class="header-anchor" href="#§-24-2-950-7-large-dollar-reporting-requirement-for-political-party-committees" aria-label="Permalink to &quot;§ 24.2-950.7 Large dollar reporting requirement for political party committees.&quot;">​</a></h1><p>In addition to the quarterly reports required by § <a href="http://law.lis.virginia.gov/vacode/24.2-950.6/" target="_blank" rel="noreferrer">24.2-950.6</a>, political party committees shall report any single contribution or loan of $10,000 or more received at any time during the calendar year within three business days of receipt of the contribution or loan.</p><ol><li><p>The report shall be filed on a &quot;large dollar contribution report&quot; form prescribed by the State Board and shall be filed in writing or electronically in the same manner as the person or committee files its quarterly disclosure reports.</p></li><li><p>Any contribution or loan reported pursuant to this section shall also be reported on the next subsequent report required under § <a href="http://law.lis.virginia.gov/vacode/24.2-950.6/" target="_blank" rel="noreferrer">24.2-950.6</a> following receipt of the contribution or loan.</p></li><li><p>For the purposes of this section, political party committees shall report as one contribution multiple contributions from a single source that have been subdivided into smaller amounts or given through different bank accounts for the purpose of evading the $10,000 threshold. A political party committee that receives contributions from affiliated organizations shall not be deemed to be receiving contributions from a single source.</p></li></ol><p>1984, c. 480, § 24.1-923; 1991, cc. 9, 709; 1993, cc. 639, 641, § 24.2-923; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0785" target="_blank" rel="noreferrer">785</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0156" target="_blank" rel="noreferrer">156</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0237" target="_blank" rel="noreferrer">237</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0026" target="_blank" rel="noreferrer">26</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',4),l=[a];function n(c,s,p,g,f,h){return r(),t("div",null,l)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
