import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 24.2-950.8 With whom political party committees file reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-950.8.md","filePath":"vacode/24.2-950.8.md"}'),a={name:"vacode/24.2-950.8.md"},o=i('<h1 id="§-24-2-950-8-with-whom-political-party-committees-file-reports" tabindex="-1">§ 24.2-950.8 With whom political party committees file reports. <a class="header-anchor" href="#§-24-2-950-8-with-whom-political-party-committees-file-reports" aria-label="Permalink to &quot;§ 24.2-950.8 With whom political party committees file reports.&quot;">​</a></h1><p>A. Except as provided in subsection B, a political party committee that is required by this chapter to file reports with the State Board, and that accepts contributions or makes expenditures in excess of $10,000 in any calendar year, or that accepted contributions or made expenditures in excess of $10,000 in the previous calendar year, shall file its reports with the State Board by computer or electronic means in accordance with the standards approved by the State Board until such time as the political party committee files a final report. Any political party committee that has been filing electronically, but does not anticipate accepting contributions or making expenditures in excess of $10,000 in the upcoming calendar year, may sign a waiver, on a form prescribed by the State Board, to exempt the committee from the electronic filing requirement for the calendar year. Such waiver form shall be submitted and received no later than the date the first report is due covering activity for that calendar year.</p><p>B. A county, city, or local district political party committee shall not be required to file by computer or electronic means if it files its reports with the general registrar of that county or city.</p><p>C. Other political party committees required to file reports by this article shall file all campaign finance reports with the State Board, if filing by electronic means, or with the State Board and the general registrar for its jurisdiction if filing campaign finance reports by nonelectronic means.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0687" target="_blank" rel="noreferrer">687</a>, § 24.2-914.1; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0242" target="_blank" rel="noreferrer">242</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>.</p>',5),n=[o];function l(c,s,p,h,f,g){return t(),r("div",null,n)}const b=e(a,[["render",l]]);export{d as __pageData,b as default};
