import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-951.4 Filing schedule for referendum committees for referendums to be decided at November elections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-951.4.md","filePath":"vacode/24.2-951.4.md"}'),o={name:"vacode/24.2-951.4.md"},a=i('<h1 id="§-24-2-951-4-filing-schedule-for-referendum-committees-for-referendums-to-be-decided-at-november-elections" tabindex="-1">§ 24.2-951.4 Filing schedule for referendum committees for referendums to be decided at November elections. <a class="header-anchor" href="#§-24-2-951-4-filing-schedule-for-referendum-committees-for-referendums-to-be-decided-at-november-elections" aria-label="Permalink to &quot;§ 24.2-951.4 Filing schedule for referendum committees for referendums to be decided at November elections.&quot;">​</a></h1><p>A. A referendum committee supporting or opposing a question on the ballot in a November election shall file the prescribed campaign finance reports as follows:</p><ol><li><p>Not later than July 15 in any year in which the referendum is not on the ballot for the period January 1 through June 30;</p></li><li><p>Not later than January 15 in any year in which the referendum is not on the ballot for the period July 1 through December 31;</p></li><li><p>In an election year, not later than April 15 for the period January 1 through March 31 and pursuant to subdivisions 4 through 9 of this section;</p></li><li><p>Not later than the eighth day before the primary date complete through the thirteenth day before the primary date;</p></li><li><p>Not later than July 15 complete through June 30;</p></li><li><p>Not later than September 15 complete through August 31;</p></li><li><p>Not later than October 15 complete through September 30;</p></li><li><p>Not later than the eighth day before the November election date complete through the thirteenth day before the election date;</p></li><li><p>Not later than the thirtieth day after the November election date complete through the twenty-third day after the election date; and</p></li><li><p>Not later than January 15 following an election year complete through December 31.</p></li></ol><p>B. A referendum committee may either (i) file a final report within six months after the referendum is held or (ii) continue as a political action committee more than six months after the referendum is held, provided that the committee submits an amended statement of organization to the State Board redesignating the committee as a political action committee and complies with the requirements for political action committees in Article 4 of this chapter including the reporting schedule set forth in § <a href="http://law.lis.virginia.gov/vacode/24.2-949.6/" target="_blank" rel="noreferrer">24.2-949.6</a>.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>, § 24.2-910.1; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',5),n=[a];function l(h,c,d,s,m,p){return t(),r("div",null,n)}const g=e(o,[["render",l]]);export{u as __pageData,g as default};
