import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-951.5 Filing schedule for referendum committees for referendums to be decided at May general elections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-951.5.md","filePath":"vacode/24.2-951.5.md"}'),a={name:"vacode/24.2-951.5.md"},o=i('<h1 id="§-24-2-951-5-filing-schedule-for-referendum-committees-for-referendums-to-be-decided-at-may-general-elections" tabindex="-1">§ 24.2-951.5 Filing schedule for referendum committees for referendums to be decided at May general elections. <a class="header-anchor" href="#§-24-2-951-5-filing-schedule-for-referendum-committees-for-referendums-to-be-decided-at-may-general-elections" aria-label="Permalink to &quot;§ 24.2-951.5 Filing schedule for referendum committees for referendums to be decided at May general elections.&quot;">​</a></h1><p>A. A referendum committee supporting or opposing a question on the ballot at a May election shall file the prescribed campaign finance reports as follows:</p><ol><li><p>Not later than the eighth day before the election date complete through the eleventh day before the election date;</p></li><li><p>Not later than June 15 of the election year complete through June 10; and</p></li><li><p>Not later than July 15 of the election year complete through June 30.</p></li></ol><p>B. A referendum committee may either (i) file a final report within six months after the referendum is held or (ii) continue as a political action committee more than six months after the referendum is held, provided that the committee submits an amended statement of organization to the State Board redesignating the committee as a political action committee and complies with the requirements for political action committees in Article 4 of this chapter including the reporting schedule set forth in § <a href="http://law.lis.virginia.gov/vacode/24.2-949.6/" target="_blank" rel="noreferrer">24.2-949.6</a>.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>, § 24.2-910.1; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',5),n=[o];function l(c,s,d,h,f,m){return t(),r("div",null,n)}const u=e(a,[["render",l]]);export{g as __pageData,u as default};
