import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-951.7 Large dollar contribution reporting requirement for referendum committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-951.7.md","filePath":"vacode/24.2-951.7.md"}'),i={name:"vacode/24.2-951.7.md"},n=o('<h1 id="§-24-2-951-7-large-dollar-contribution-reporting-requirement-for-referendum-committees" tabindex="-1">§ 24.2-951.7 Large dollar contribution reporting requirement for referendum committees. <a class="header-anchor" href="#§-24-2-951-7-large-dollar-contribution-reporting-requirement-for-referendum-committees" aria-label="Permalink to &quot;§ 24.2-951.7 Large dollar contribution reporting requirement for referendum committees.&quot;">​</a></h1><p>In addition to the reports required by §§ <a href="http://law.lis.virginia.gov/vacode/24.2-951.4/" target="_blank" rel="noreferrer">24.2-951.4</a>, <a href="http://law.lis.virginia.gov/vacode/24.2-951.5/" target="_blank" rel="noreferrer">24.2-951.5</a> and <a href="http://law.lis.virginia.gov/vacode/24.2-951.6/" target="_blank" rel="noreferrer">24.2-951.6</a>, referendum committees shall report any single contribution or loan of $10,000 or more received at any time during the calendar year within three business days of receipt of the contribution or loan.</p><ol><li><p>The report shall be filed on a &quot;large dollar contribution report&quot; form prescribed by the State Board and shall be filed in writing or electronically in the same manner as the person or committee files its quarterly disclosure reports.</p></li><li><p>Any contribution or loan reported pursuant to this section shall also be reported on the next subsequent report required under this article following receipt of the contribution or loan.</p></li><li><p>For the purposes of this section, referendum committees shall report as one contribution multiple contributions from a single source that have been subdivided into smaller amounts or given through different bank accounts for the purpose of evading the $10,000 threshold.</p></li><li><p>A referendum committee that receives contributions from affiliated organizations shall not be deemed to be receiving contributions from a single source.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>, § 24.2-910.1; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',4),a=[n];function l(s,c,d,u,f,p){return r(),t("div",null,a)}const g=e(i,[["render",l]]);export{m as __pageData,g as default};
