import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 24.2-951.8 With whom referendum committees file reports; electronic filing requirement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-951.8.md","filePath":"vacode/24.2-951.8.md"}'),a={name:"vacode/24.2-951.8.md"},n=i('<h1 id="§-24-2-951-8-with-whom-referendum-committees-file-reports-electronic-filing-requirement" tabindex="-1">§ 24.2-951.8 With whom referendum committees file reports; electronic filing requirement. <a class="header-anchor" href="#§-24-2-951-8-with-whom-referendum-committees-file-reports-electronic-filing-requirement" aria-label="Permalink to &quot;§ 24.2-951.8 With whom referendum committees file reports; electronic filing requirement.&quot;">​</a></h1><p>A. Referendum committees required to file statements or reports by this article shall file all reports with the State Board.</p><p>B. A referendum committee that is required by this chapter to file reports with the State Board, and that accepts contributions or makes expenditures in excess of $10,000 in any calendar year, or that accepted contributions or made expenditures in excess of $10,000 in the previous calendar year, shall file its reports with the State Board by computer or electronic means in accordance with the standards approved by the State Board until such time as the referendum committee files a final report or until subject to the provisions of subsection B of § <a href="http://law.lis.virginia.gov/vacode/24.2-951.1/" target="_blank" rel="noreferrer">24.2-951.1</a>.</p><p>C. Any referendum committee that has been filing electronically, but does not anticipate accepting contributions or making expenditures in excess of $10,000 in the upcoming calendar year, may sign a waiver, on a form prescribed by the State Board, to exempt the committee from the electronic filing requirement for the calendar year. Such waiver form shall be submitted and received no later than the date the first report is due covering activity for that calendar year.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0687" target="_blank" rel="noreferrer">687</a>, § 24.2-914.1; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0242" target="_blank" rel="noreferrer">242</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>, § 24.2-910.1; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',5),o=[n];function l(c,s,f,h,g,p){return r(),t("div",null,o)}const u=e(a,[["render",l]]);export{d as __pageData,u as default};
