import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 24.2-952.1 Statement of organization for an inaugural committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-952.1.md","filePath":"vacode/24.2-952.1.md"}'),i={name:"vacode/24.2-952.1.md"},n=o('<h1 id="§-24-2-952-1-statement-of-organization-for-an-inaugural-committee" tabindex="-1">§ 24.2-952.1 Statement of organization for an inaugural committee. <a class="header-anchor" href="#§-24-2-952-1-statement-of-organization-for-an-inaugural-committee" aria-label="Permalink to &quot;§ 24.2-952.1 Statement of organization for an inaugural committee.&quot;">​</a></h1><p>Each inaugural committee shall file with the State Board a statement of organization within 10 days after its organization. Any change in information previously submitted in a statement of organization shall be reported to the State Board within 10 days following the change.</p><p>The statement of organization shall include:</p><ol><li><p>The name of the committee and its address in the Commonwealth;</p></li><li><p>The name and business address of the treasurer and his residence address in the Commonwealth who shall be deemed the agent of the inaugural committee for the purpose of service of process on the inaugural committee;</p></li><li><p>The name, residence address in the Commonwealth, business address, and position of the custodian of the books and accounts who works under the direction of the treasurer and the address where the books are maintained, if different from the business address of the custodian of the books and accounts;</p></li><li><p>The name and residence address of the elected official for whose inauguration the committee is organized;</p></li><li><p>The designated depository to be used for the receipt and holding of funds and contributions received by the committee, in an account in a financial institution within the Commonwealth; and</p></li><li><p>Such other information as shall be required by the State Board except that the account number for the designated depository account shall not be required.</p></li></ol><p>1991, cc. 474, 709, § 24.1-254.3; 1993, c. 641, § 24.2-913; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',5),r=[n];function s(d,h,c,l,m,f){return t(),a("div",null,r)}const g=e(i,[["render",s]]);export{p as __pageData,g as default};
