import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 24.2-953.1 Failure to file the required reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-953.1.md","filePath":"vacode/24.2-953.1.md"}'),i={name:"vacode/24.2-953.1.md"},o=r('<h1 id="§-24-2-953-1-failure-to-file-the-required-reports" tabindex="-1">§ 24.2-953.1 Failure to file the required reports. <a class="header-anchor" href="#§-24-2-953-1-failure-to-file-the-required-reports" aria-label="Permalink to &quot;§ 24.2-953.1 Failure to file the required reports.&quot;">​</a></h1><p>A. In the case of a failure to file the statement of organization for a candidate campaign committee or political committee required by this chapter, there shall be a civil penalty not to exceed $500.</p><p>B. In the case of the failure to file a required report, the candidate campaign committee or political committee shall be assessed a civil penalty not to exceed $500. In the case of the failure to file a report required pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/24.2-949.6/" target="_blank" rel="noreferrer">24.2-949.6</a>, the political action committee shall be assessed a civil penalty not to exceed $500. In the case of a second or any subsequent such violation pertaining to one election cycle, the candidate campaign committee or political committee shall be assessed a civil penalty of $1,000 for each such failure to file.</p><p>C. In the case of a failure to file the report of any large pre-election contribution required by § <a href="http://law.lis.virginia.gov/vacode/24.2-947.9/" target="_blank" rel="noreferrer">24.2-947.9</a> or a report required pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/24.2-949.6/" target="_blank" rel="noreferrer">24.2-949.6</a>, there shall be a rebuttable presumption that the violation was willful.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0696" target="_blank" rel="noreferrer">696</a>.</p>',5),l=[o];function n(c,s,f,p,h,d){return t(),a("div",null,l)}const g=e(i,[["render",n]]);export{_ as __pageData,g as default};
