import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 24.2-955.3 Penalties for violations of this chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-955.3.md","filePath":"vacode/24.2-955.3.md"}'),r={name:"vacode/24.2-955.3.md"},o=i('<h1 id="§-24-2-955-3-penalties-for-violations-of-this-chapter" tabindex="-1">§ 24.2-955.3 Penalties for violations of this chapter. <a class="header-anchor" href="#§-24-2-955-3-penalties-for-violations-of-this-chapter" aria-label="Permalink to &quot;§ 24.2-955.3 Penalties for violations of this chapter.&quot;">​</a></h1><p>A. Any sponsor violating Article 2 (§ <a href="/vacode/24.2-956/">24.2-956</a> et seq.) shall be subject to a civil penalty not to exceed $25,000. In the case of a willful violation, he is guilty of a Class 1 misdemeanor.</p><p>B. Any sponsor violating Article 3 (§ <a href="/vacode/24.2-957/">24.2-957</a> et seq.) or 4 (§ <a href="/vacode/24.2-958/">24.2-958</a> et seq.) shall be subject to a civil penalty not to exceed $25,000. In the case of a willful violation, he is guilty of a Class 1 misdemeanor. In no event shall the total civil penalties imposed for multiple broadcasts of one particular campaign advertisement exceed $25,000.</p><p>C. Any person violating Article 5 (§ <a href="/vacode/24.2-959/">24.2-959</a> et seq.) shall be subject to a civil penalty not to exceed $25,000, and in the case of a willful violation, he is guilty of a Class 1 misdemeanor. A violation of the provisions of Article 5 shall not void any election.</p><p>D. The State Board, in a public hearing, shall determine whether to find a violation of this chapter and to assess a civil penalty. At least 10 days prior to such hearing, the State Board shall send notice by certified mail to persons whose actions will be reviewed at such meeting and may be subject to civil penalty. Notice shall include the time and date of the meeting, an explanation of the violation, and the maximum civil penalty that may be assessed.</p><p>E. It shall not be deemed a violation of this chapter if the contents of the disclosure legend or statement convey the required information.</p><p>F. Any civil penalties collected pursuant to an action under this section shall be payable to the State Treasurer for deposit to the general fund. The procedure to enforce the civil penalties provided in this section shall be as stated in § <a href="/vacode/24.2-946.3/">24.2-946.3</a>.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>, §§ <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0024" target="_blank" rel="noreferrer">24</a>.2-943, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0024" target="_blank" rel="noreferrer">24</a>.2-944; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0237" target="_blank" rel="noreferrer">237</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0546" target="_blank" rel="noreferrer">546</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>.</p>',8),l=[o];function n(s,c,h,f,p,g){return a(),t("div",null,l)}const u=e(r,[["render",n]]);export{v as __pageData,u as default};
