import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 24.2-957 General provisions; applicability to advertisements in video format.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-957.md","filePath":"vacode/24.2-957.md"}'),a={name:"vacode/24.2-957.md"},o=r('<h1 id="§-24-2-957-general-provisions-applicability-to-advertisements-in-video-format" tabindex="-1">§ 24.2-957 General provisions; applicability to advertisements in video format. <a class="header-anchor" href="#§-24-2-957-general-provisions-applicability-to-advertisements-in-video-format" aria-label="Permalink to &quot;§ 24.2-957 General provisions; applicability to advertisements in video format.&quot;">​</a></h1><p>A. Television outlets and online platforms shall not be liable under this article for carriage of political advertisements that fail to include the disclosure requirements provided for in this article. This provision supersedes any contrary provisions of the Code of Virginia.</p><p>B. If the sponsor does not have the option of controlling the audio, if any, heard during the television advertisement, the disclosure requirements shall be the same as for print media.</p><p>C. The person accepting an advertisement for a television outlet shall require, and for one year shall retain a copy of, proof of identity of the person who submits the advertisement for broadcast. Proof of identity shall be submitted either (i) in person and include a valid Virginia driver&#39;s license, or any other identification card issued by a government agency of the Commonwealth, one of its political subdivisions, or the United States, or (ii) other than in person, in which case, the person submitting the advertisement shall provide a telephone number and the person accepting the advertisement may phone the person to verify the validity of the person&#39;s identifying information before broadcasting the advertisement.</p><p>D. Any disclosure statement required by this article shall be displayed in a conspicuous manner.</p><p>E. An advertisement that is in video format and is placed or promoted for a fee on an online platform shall be subject to the same disclosure requirements to which television advertisements are subject pursuant to this article.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>, § 24.2-944; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',7),n=[o];function s(l,p,d,c,h,f){return i(),t("div",null,n)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
