import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-958.3 Requirements for radio advertisements sponsored by a person that is not a candidate or political committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-958.3.md","filePath":"vacode/24.2-958.3.md"}'),a={name:"vacode/24.2-958.3.md"},r=i('<h1 id="§-24-2-958-3-requirements-for-radio-advertisements-sponsored-by-a-person-that-is-not-a-candidate-or-political-committee" tabindex="-1">§ 24.2-958.3 Requirements for radio advertisements sponsored by a person that is not a candidate or political committee. <a class="header-anchor" href="#§-24-2-958-3-requirements-for-radio-advertisements-sponsored-by-a-person-that-is-not-a-candidate-or-political-committee" aria-label="Permalink to &quot;§ 24.2-958.3 Requirements for radio advertisements sponsored by a person that is not a candidate or political committee.&quot;">​</a></h1><p>A. It shall be unlawful for a person to sponsor an advertisement that constitutes an expenditure or contribution required to be disclosed under Chapter 9.3 (§ <a href="http://law.lis.virginia.gov/vacode/24.2-945/" target="_blank" rel="noreferrer">24.2-945</a> et seq.) unless the following requirements are met:</p><ol><li><p>Radio advertisements purchased by an individual supporting or opposing the nomination or election of one or more clearly identified candidates shall include a disclosure statement spoken by the individual containing at least the following words: &quot;I am [individual&#39;s name], and I sponsored this ad.&quot;</p></li><li><p>Radio advertisements purchased by a corporation, partnership, business, labor organization, membership organization, association, cooperative, or other like entity supporting or opposing the nomination or election of one or more clearly identified candidates shall include a disclosure statement spoken by the chief executive of the sponsor containing at least the following words: &quot;[Name of sponsor] paid for (or &#39;sponsored&#39; or &#39;furnished&#39;) this ad.&quot;</p></li></ol><p>B. In its oral disclosure statement, a person may choose to identify an advertisement as either supporting or opposing the nomination or election of one or more clearly identified candidates.</p><p>C. If an advertisement is jointly sponsored, the disclosure statement shall include the names of all the sponsors.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>, § 24.2-944; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>.</p>',6),n=[r];function s(l,d,p,c,h,m){return t(),o("div",null,n)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
