import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 25.1-102 Condemnation of property of corporations possessing power of eminent domain.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-102.md","filePath":"vacode/25.1-102.md"}'),n={name:"vacode/25.1-102.md"},a=t('<h1 id="§-25-1-102-condemnation-of-property-of-corporations-possessing-power-of-eminent-domain" tabindex="-1">§ 25.1-102 Condemnation of property of corporations possessing power of eminent domain. <a class="header-anchor" href="#§-25-1-102-condemnation-of-property-of-corporations-possessing-power-of-eminent-domain" aria-label="Permalink to &quot;§ 25.1-102 Condemnation of property of corporations possessing power of eminent domain.&quot;">​</a></h1><p>A. Except as provided in §§ <a href="http://law.lis.virginia.gov/vacode/15.2-1906/" target="_blank" rel="noreferrer">15.2-1906</a> and <a href="http://law.lis.virginia.gov/vacode/15.2-2146/" target="_blank" rel="noreferrer">15.2-2146</a>, no (i) corporation or (ii) electric authority created under the provisions of Chapter 54 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-5400/" target="_blank" rel="noreferrer">15.2-5400</a> et seq.) of Title 15.2 shall file a petition to take by condemnation proceedings any property belonging to any other corporation possessing the power of eminent domain, unless, after notice to all parties in interest and an opportunity for a hearing, the State Corporation Commission shall certify that a public necessity or that an essential public convenience shall so require, and shall give its permission thereto; and in no event shall one corporation take by condemnation proceedings any property owned by and essential to the purposes of another corporation possessing the power of eminent domain. Notwithstanding anything herein to the contrary, a locality exercising the powers granted by § <a href="http://law.lis.virginia.gov/vacode/15.2-2109/" target="_blank" rel="noreferrer">15.2-2109</a> or § <a href="http://law.lis.virginia.gov/vacode/15.2-2115/" target="_blank" rel="noreferrer">15.2-2115</a> shall be subject to the provisions of this section to the same extent as are corporations, unless otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/15.2-1906/" target="_blank" rel="noreferrer">15.2-1906</a> or § <a href="http://law.lis.virginia.gov/vacode/15.2-2146/" target="_blank" rel="noreferrer">15.2-2146</a>.</p><p>B. If the State Corporation Commission gives its permission to a condemnation, the Commission shall establish for use in any condemnation proceeding whether any payment for stranded investment is appropriate and, if so, the amount of such payment and any conditions thereof.</p><p>C. Any condemnor that is authorized to use the procedure set out in Chapter 3 (§ <a href="http://law.lis.virginia.gov/vacode/25.1-300/" target="_blank" rel="noreferrer">25.1-300</a> et seq.) of this title by a provision that incorporates such procedure by reference shall, in using such procedure, be subject to the provisions of this section to the same extent as are corporations, unless the provision specifically provides that this section shall not apply to such condemnor&#39;s use of such procedure.</p><p>Code 1919, § 3832; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0619" target="_blank" rel="noreferrer">619</a>, § 25-233; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0484" target="_blank" rel="noreferrer">484</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0531" target="_blank" rel="noreferrer">531</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>.</p>',5),i=[a];function s(p,l,c,h,d,f){return o(),r("div",null,i)}const m=e(n,[["render",s]]);export{v as __pageData,m as default};
