import{_ as n,o,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 25.1-218 Intervention in proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-218.md","filePath":"vacode/25.1-218.md"}'),i={name:"vacode/25.1-218.md"},a=e("h1",{id:"§-25-1-218-intervention-in-proceedings",tabindex:"-1"},[t("§ 25.1-218 Intervention in proceedings. "),e("a",{class:"header-anchor",href:"#§-25-1-218-intervention-in-proceedings","aria-label":'Permalink to "§ 25.1-218 Intervention in proceedings."'},"​")],-1),s=e("p",null,"Any person not already a party to the proceedings whose property, or any interest or estate therein, is to be taken or damaged, or who claims that his other property, or any interest therein will be damaged as a result of the taking and use by the petitioner, may be made a party to the proceeding upon filing a petition for intervention by leave of court (i) at any time prior to the beginning of the trial of the issue of just compensation, or (ii) in the discretion of the court, at such other times during the pendency of the proceeding upon such terms and conditions as the court deems proper, considering all the circumstances at that time. Such a person intervening in the proceeding shall be permitted to assert any claim or defense then germane to the proceeding upon such terms and conditions as the court deems reasonable and proper.",-1),c=e("p",null,[t("Code 1919, § 4383; Code 1950, § 25-42; 1962, c. 426, § 25-46.16; 2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940",target:"_blank",rel:"noreferrer"},"940"),t(".")],-1),d=[a,s,c];function p(h,l,m,g,u,f){return o(),r("div",null,d)}const y=n(i,[["render",p]]);export{v as __pageData,y as default};
