import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 25.1-222 Proceedings not to be delayed by claims with respect to ownership of property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-222.md","filePath":"vacode/25.1-222.md"}'),a={name:"vacode/25.1-222.md"},i=o('<h1 id="§-25-1-222-proceedings-not-to-be-delayed-by-claims-with-respect-to-ownership-of-property" tabindex="-1">§ 25.1-222 Proceedings not to be delayed by claims with respect to ownership of property. <a class="header-anchor" href="#§-25-1-222-proceedings-not-to-be-delayed-by-claims-with-respect-to-ownership-of-property" aria-label="Permalink to &quot;§ 25.1-222 Proceedings not to be delayed by claims with respect to ownership of property.&quot;">​</a></h1><p>No delay in the proceeding for the determination of just compensation shall be occasioned by the claims of the parties with respect to the ownership of any land or other property or to the interest therein of the respective parties. In such cases the court shall require the retention of the deposit of the award for the whole property, or the part in dispute, until the rights of the respective parties have been determined in the manner hereinafter provided in § <a href="http://law.lis.virginia.gov/vacode/25.1-241/" target="_blank" rel="noreferrer">25.1-241</a>; provided, however, the court shall permit any such claimants to intervene as parties to the proceedings as provided in § <a href="http://law.lis.virginia.gov/vacode/25.1-218/" target="_blank" rel="noreferrer">25.1-218</a>.</p><p>1962, c. 426, § 25-46.18; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>.</p>',3),n=[i];function s(p,c,h,d,l,_){return t(),r("div",null,n)}const v=e(a,[["render",s]]);export{m as __pageData,v as default};
