import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 25.1-308 Effect of recordation of certificate; transfer of title or interest in property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-308.md","filePath":"vacode/25.1-308.md"}'),i={name:"vacode/25.1-308.md"},a=o('<h1 id="§-25-1-308-effect-of-recordation-of-certificate-transfer-of-title-or-interest-in-property" tabindex="-1">§ 25.1-308 Effect of recordation of certificate; transfer of title or interest in property. <a class="header-anchor" href="#§-25-1-308-effect-of-recordation-of-certificate-transfer-of-title-or-interest-in-property" aria-label="Permalink to &quot;§ 25.1-308 Effect of recordation of certificate; transfer of title or interest in property.&quot;">​</a></h1><p>A. Upon recordation of a certificate:</p><ol><li><p>The interest or estate of the owner of the property described therein shall terminate;</p></li><li><p>The title to such property shall be vested in the authorized condemnor;</p></li><li><p>The owner shall have such interest or estate in the funds deposited with the court or represented by the certificate of deposit as the owner had in the property taken or damaged; and</p></li><li><p>All liens by deed of trust, judgment or otherwise upon such property shall be transferred to such funds.</p></li></ol><p>B. The title in the authorized condemnor shall be defeasible until (i) the authorized condemnor and such owner reach an agreement as provided in § <a href="/vacode/25.1-317/">25.1-317</a>, or (ii) the compensation for the taking or damage to the property is determined by condemnation proceedings as provided in § <a href="/vacode/25.1-313/">25.1-313</a>.</p><p>C. If funds have been deposited with the court under a certificate of take, the clerk shall deposit the funds so paid to the credit of the court in an account of a type that bears interest.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',6),n=[a];function c(s,f,d,p,h,l){return t(),r("div",null,n)}const m=e(i,[["render",c]]);export{u as __pageData,m as default};
