import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 25.1-311 Effect of acceptance of payments; evidence as to amount of deposit or certificate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-311.md","filePath":"vacode/25.1-311.md"}'),n={name:"vacode/25.1-311.md"},r=o('<h1 id="§-25-1-311-effect-of-acceptance-of-payments-evidence-as-to-amount-of-deposit-or-certificate" tabindex="-1">§ 25.1-311 Effect of acceptance of payments; evidence as to amount of deposit or certificate. <a class="header-anchor" href="#§-25-1-311-effect-of-acceptance-of-payments-evidence-as-to-amount-of-deposit-or-certificate" aria-label="Permalink to &quot;§ 25.1-311 Effect of acceptance of payments; evidence as to amount of deposit or certificate.&quot;">​</a></h1><p>A. The acceptance of payment as provided in § <a href="http://law.lis.virginia.gov/vacode/25.1-310/" target="_blank" rel="noreferrer">25.1-310</a> shall not limit the amount to be allowed by the body determining just compensation in a condemnation proceeding, nor limit the rights of any party or parties to the proceeding to appeal from any decision therein.</p><p>B. A party to a condemnation proceeding shall not be entitled to introduce evidence of any amount deposited with the court or represented by a certificate, nor of any amount that has been accepted by any party entitled thereto pursuant to § <a href="http://law.lis.virginia.gov/vacode/25.1-310/" target="_blank" rel="noreferrer">25.1-310</a>.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>.</p>',4),c=[r];function i(p,s,f,d,l,_){return t(),a("div",null,c)}const u=e(n,[["render",i]]);export{m as __pageData,u as default};
