import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 25.1-407 Optional moving expense allowance for persons displaced from dwelling.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-407.md","filePath":"vacode/25.1-407.md"}'),r={name:"vacode/25.1-407.md"},t=n('<h1 id="§-25-1-407-optional-moving-expense-allowance-for-persons-displaced-from-dwelling" tabindex="-1">§ 25.1-407 Optional moving expense allowance for persons displaced from dwelling. <a class="header-anchor" href="#§-25-1-407-optional-moving-expense-allowance-for-persons-displaced-from-dwelling" aria-label="Permalink to &quot;§ 25.1-407 Optional moving expense allowance for persons displaced from dwelling.&quot;">​</a></h1><p>Any displaced person eligible for payments under § <a href="http://law.lis.virginia.gov/vacode/25.1-406/" target="_blank" rel="noreferrer">25.1-406</a> who is displaced from a dwelling may elect to accept the payments authorized by this section in lieu of the payments authorized by § <a href="http://law.lis.virginia.gov/vacode/25.1-406/" target="_blank" rel="noreferrer">25.1-406</a>, which displaced person so electing shall receive a moving expense allowance of an amount determined according to a schedule established by the state agency. The acceptance of the payment authorized by this section shall be in lieu of any payment under § <a href="http://law.lis.virginia.gov/vacode/25.1-406/" target="_blank" rel="noreferrer">25.1-406</a>.</p><p>Code 1950, §§ 33-75.04, 33.1-132.4; 1970, c. 40, § 25-239; 1972, c. 738; 1989, c. 714; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>.</p>',3),i=[t];function l(s,c,d,p,h,f){return a(),o("div",null,i)}const m=e(r,[["render",l]]);export{_ as __pageData,m as default};
