import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 25.1-410 Replacement housing for tenants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-410.md","filePath":"vacode/25.1-410.md"}'),o={name:"vacode/25.1-410.md"},i=n('<h1 id="§-25-1-410-replacement-housing-for-tenants" tabindex="-1">§ 25.1-410 Replacement housing for tenants. <a class="header-anchor" href="#§-25-1-410-replacement-housing-for-tenants" aria-label="Permalink to &quot;§ 25.1-410 Replacement housing for tenants.&quot;">​</a></h1><p>A. In addition to amounts otherwise authorized by this article, a state agency shall make a payment to or for any displaced person displaced from any dwelling not eligible to receive a payment under § <a href="http://law.lis.virginia.gov/vacode/25.1-409/" target="_blank" rel="noreferrer">25.1-409</a> which dwelling was actually and lawfully occupied by such displaced person for not less than 90 days immediately prior to (i) the initiation of negotiations for acquisition of the dwelling or (ii) if the displacement is not a direct result of acquisition, such other event as the state agency shall prescribe. Such payment shall consist of the amount necessary to enable such displaced person to lease or rent, for a period not to exceed 42 months, a comparable replacement dwelling, but not to exceed $7,200. At the discretion of the state agency, a payment under this subsection may be made in periodic installments. Computation of a payment under this subsection to a low-income displaced person for a comparable replacement dwelling shall take into account such person&#39;s income.</p><p>B. Any person eligible for a payment under subsection A may elect to apply such payment to a down payment on, and other incidental expenses pursuant to, the purchase of a decent, safe, and sanitary replacement dwelling. Any such person may, at the discretion of the state agency, be eligible under this subsection for the maximum payment allowed under subsection A.</p><p>1972, c. 738, § 25-241; 1989, c. 714; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0218" target="_blank" rel="noreferrer">218</a>.</p>',4),r=[i];function s(l,c,p,d,h,u){return t(),a("div",null,r)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
