import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 25.1-414 Authority of state agency where replacement housing not available; requiring person to move.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-414.md","filePath":"vacode/25.1-414.md"}'),o={name:"vacode/25.1-414.md"},n=r('<h1 id="§-25-1-414-authority-of-state-agency-where-replacement-housing-not-available-requiring-person-to-move" tabindex="-1">§ 25.1-414 Authority of state agency where replacement housing not available; requiring person to move. <a class="header-anchor" href="#§-25-1-414-authority-of-state-agency-where-replacement-housing-not-available-requiring-person-to-move" aria-label="Permalink to &quot;§ 25.1-414 Authority of state agency where replacement housing not available; requiring person to move.&quot;">​</a></h1><p>A. If a program or project undertaken by a state agency cannot proceed to actual construction on a timely basis because comparable replacement dwellings are not available, and the state agency determines that such dwellings cannot otherwise be made available, the state agency may take such action as is necessary or appropriate to provide such dwellings by use of funds authorized for such project. The state agency may use this section to exceed the maximum amounts that may be paid under §§ <a href="http://law.lis.virginia.gov/vacode/25.1-409/" target="_blank" rel="noreferrer">25.1-409</a> and <a href="http://law.lis.virginia.gov/vacode/25.1-410/" target="_blank" rel="noreferrer">25.1-410</a> on a case-by-case basis for good cause as determined in accordance with such regulations as the state agency shall issue.</p><p>B. No person shall be required to move from his dwelling on account of any project, unless the head of the state agency is satisfied that comparable replacement housing is available to such person.</p><p>1972, c. 738, § 25-247; 1989, c. 714; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>.</p>',4),i=[n];function s(c,l,h,g,p,u){return a(),t("div",null,i)}const f=e(o,[["render",s]]);export{m as __pageData,f as default};
