import{_ as e,o as r,c as n,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 25.1-420 Reimbursement of owner for costs incurred in inverse condemnation proceeding.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/25.1-420.md","filePath":"vacode/25.1-420.md"}'),o={name:"vacode/25.1-420.md"},a=t('<h1 id="§-25-1-420-reimbursement-of-owner-for-costs-incurred-in-inverse-condemnation-proceeding" tabindex="-1">§ 25.1-420 Reimbursement of owner for costs incurred in inverse condemnation proceeding. <a class="header-anchor" href="#§-25-1-420-reimbursement-of-owner-for-costs-incurred-in-inverse-condemnation-proceeding" aria-label="Permalink to &quot;§ 25.1-420 Reimbursement of owner for costs incurred in inverse condemnation proceeding.&quot;">​</a></h1><p>If a declaratory judgment proceeding is instituted pursuant to § <a href="http://law.lis.virginia.gov/vacode/8.01-187/" target="_blank" rel="noreferrer">8.01-187</a> by the owner of any right, title or interest in real property because of use of his property in any program or project undertaken by a state agency, and either (i) the court renders a judgment for the plaintiff in such proceeding and awards compensation for the damaging or taking of property or (ii) the Attorney General effects a settlement of any such proceeding in which the Commonwealth is a party, the court or Attorney General, as appropriate, shall determine and award or allow to such plaintiff, as a part of such judgment or settlement, such sum as will, in the opinion of the court or the Attorney General, as the case may be, reimburse such plaintiff for his reasonable costs, disbursements and expenses, including reasonable attorney, appraisal and engineering fees, actually incurred because of such proceeding.</p><p>1972, c. 738, § 25-251; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',3),i=[a];function s(c,d,l,p,f,u){return r(),n("div",null,i)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
