import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 27-15.2 Purchase, maintenance, etc., of equipment; donated equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/27-15.2.md","filePath":"vacode/27-15.2.md"}'),a={name:"vacode/27-15.2.md"},r=i('<h1 id="§-27-15-2-purchase-maintenance-etc-of-equipment-donated-equipment" tabindex="-1">§ 27-15.2 Purchase, maintenance, etc., of equipment; donated equipment. <a class="header-anchor" href="#§-27-15-2-purchase-maintenance-etc-of-equipment-donated-equipment" aria-label="Permalink to &quot;§ 27-15.2 Purchase, maintenance, etc., of equipment; donated equipment.&quot;">​</a></h1><p>A. The governing body of every county, city, and town shall have power to provide for the purchase, operation, staffing, and maintenance of suitable equipment for firefighting or performing emergency medical services in or upon the property of the county, city, or town and of its inhabitants, and to prescribe the terms and conditions upon which the same will be used for fighting fires or performing emergency medical services in or upon privately owned property. All equipment purchased after October 1, 1970, shall be equipped with threads of USA Standard B2.3, B2.4 of the American Standards Association.</p><p>B. Any fire department of a county, city, or town, or any fire company donating equipment for fighting fires to any fire department or any fire company, which equipment met existing engineering and safety standards at the time of its purchase by the donating entity, shall be immune from civil liability unless the donating entity acted with gross negligence or willful misconduct.</p><p>C. A safety inspection shall be completed by a certified emergency vehicle service center and a report designating any deficiencies shall be provided prior to the change in ownership of the donated emergency vehicle.</p><p>1970, c. 187; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0545" target="_blank" rel="noreferrer">545</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',5),o=[r];function c(s,p,d,l,f,h){return t(),n("div",null,o)}const u=e(a,[["render",c]]);export{g as __pageData,u as default};
