import{_ as n,o as t,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 27-40.4 Income earned during disability retirement; reduction in benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/27-40.4.md","filePath":"vacode/27-40.4.md"}'),r={name:"vacode/27-40.4.md"},s=e("h1",{id:"§-27-40-4-income-earned-during-disability-retirement-reduction-in-benefits",tabindex:"-1"},[o("§ 27-40.4 Income earned during disability retirement; reduction in benefits. "),e("a",{class:"header-anchor",href:"#§-27-40-4-income-earned-during-disability-retirement-reduction-in-benefits","aria-label":'Permalink to "§ 27-40.4 Income earned during disability retirement; reduction in benefits."'},"​")],-1),a=e("p",null,'If any person is entitled to and receives pensions and benefits under this article and subsequently becomes employed, whether full-time or part-time, the pensions and benefits received shall be reduced by the amount of income received which exceeds the difference between the benefits received under this article and the amount of pay to which he would have been entitled had his employment progressed in the same rank and grade with credit for the level of seniority he would have attained had he not been disabled. Such a reduction in benefits shall continue until the time he becomes eligible for normal retirement, had he remained uninjured and continued his employment. For the purposes of this section, "income" means gross income received less deductions for social security taxes only.',-1),d=e("p",null,"Any person receiving pensions or benefits under the provisions of this article shall upon request, on or before May 1 of each year, provide a true and correct copy of all W-2 forms showing income received, or a statement under oath as to whether he has received compensation for work performed in the previous calendar year, to the governing body of the jurisdiction which is the source of the pension, or to that body's designee. Refusal to provide such documents shall be grounds for termination of benefits under this section, until such documents are produced. Production of the documents shall be required only until such time as the person shall be eligible for normal retirement, had he remained uninjured and continued his employment.",-1),c=e("p",null,"Nothing contained herein shall limit or restrict the right of any person to receive workers' compensation benefits under Title 65.2, as amended.",-1),h=e("p",null,"1983, c. 351.",-1),l=[s,a,d,c,h];function u(m,f,p,b,y,_){return t(),i("div",null,l)}const w=n(r,[["render",u]]);export{g as __pageData,w as default};
