import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 27-9 Organization of fire company.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/27-9.md","filePath":"vacode/27-9.md"}'),r={name:"vacode/27-9.md"},i=a('<h1 id="§-27-9-organization-of-fire-company" tabindex="-1">§ 27-9 Organization of fire company. <a class="header-anchor" href="#§-27-9-organization-of-fire-company" aria-label="Permalink to &quot;§ 27-9 Organization of fire company.&quot;">​</a></h1><p>A writing stating the formation of a fire company, with the names of the members thereof thereto subscribed, shall be recorded in the court of the city or the court of the county wherein such fire company is located, after which the members of the fire company may make regulations for effecting its objects consistent with the laws of the Commonwealth, the ordinances of the county, city, or town and the bylaws of the fire department thereof. The principal officer of such fire company shall be known as &quot;the chief.&quot;</p><p>Code 1919, § 3122; 1970, c. 187; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',3),n=[i];function c(f,h,s,l,p,_){return t(),o("div",null,n)}const g=e(r,[["render",c]]);export{d as __pageData,g as default};
