import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 28.2-106 Virginia Marine Police; law-enforcement responsibilities; qualifications; oath.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-106.md","filePath":"vacode/28.2-106.md"}'),o={name:"vacode/28.2-106.md"},n=t('<h1 id="§-28-2-106-virginia-marine-police-law-enforcement-responsibilities-qualifications-oath" tabindex="-1">§ 28.2-106 Virginia Marine Police; law-enforcement responsibilities; qualifications; oath. <a class="header-anchor" href="#§-28-2-106-virginia-marine-police-law-enforcement-responsibilities-qualifications-oath" aria-label="Permalink to &quot;§ 28.2-106 Virginia Marine Police; law-enforcement responsibilities; qualifications; oath.&quot;">​</a></h1><p>A. The law-enforcement division of the Commission shall be designated as the Virginia Marine Police. It shall exercise such powers and duties as the General Assembly may confer upon it by law and as provided in regulations adopted pursuant to law, including but not limited to:</p><ol><li><p>Patrolling the tidal waters and shoreline of the Chesapeake Bay, its tidal tributaries, and territorial sea;</p></li><li><p>Enforcing marine fishery and habitat conservation laws and regulations;</p></li><li><p>Enforcing health laws pertaining to the harvesting of seafood from condemned areas;</p></li><li><p>Enforcing or assisting other agencies in enforcing laws pertaining to the removal of obstructions and abandoned vessels from the water, to boating operation and navigation, and to larceny on the water;</p></li><li><p>Providing for water-borne safety;</p></li><li><p>Conducting search and rescue activities; and</p></li><li><p>Protecting from terrorist attack federal and state water-related installations and other water-related locations within the tidal waters of the Commonwealth as may be designated by federal or state officials as important to national security.</p></li></ol><p>B. Officers of the Virginia Marine Police shall have the same powers as (i) sheriffs and other law-enforcement officers to enforce all of the criminal laws of the Commonwealth, and (ii) regular conservation police officers appointed pursuant to Chapter 2 (§ <a href="http://law.lis.virginia.gov/vacode/29.1-200/" target="_blank" rel="noreferrer">29.1-200</a> et seq.) of Title 29.1.</p><p>C. A person shall be (i) at least twenty-one years old and (ii) a high school graduate or equivalent to qualify for appointment as an officer.</p><p>D. Each officer shall qualify before the clerk of the circuit court of the county or city in which he resides, or in which his district may be, by taking the oaths prescribed by law.</p><p>Code 1950, § 28-36; 1962, c. 406, §§ 28.1-41, 28.1-42; 1964, c. 115; 1972, c. 824; 1973, c. 19; 1990, c. 521, § 28.1-45.1; 1991, c. 338, § 28.1-45.2; 1992, c. 836; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0232" target="_blank" rel="noreferrer">232</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>.</p>',7),r=[n];function s(l,c,h,f,d,p){return i(),a("div",null,r)}const u=e(o,[["render",s]]);export{m as __pageData,u as default};
