import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 28.2-1100 Virginia Institute of Marine Science continued; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-1100.md","filePath":"vacode/28.2-1100.md"}'),a={name:"vacode/28.2-1100.md"},o=n('<h1 id="§-28-2-1100-virginia-institute-of-marine-science-continued-duties" tabindex="-1">§ 28.2-1100 Virginia Institute of Marine Science continued; duties. <a class="header-anchor" href="#§-28-2-1100-virginia-institute-of-marine-science-continued-duties" aria-label="Permalink to &quot;§ 28.2-1100 Virginia Institute of Marine Science continued; duties.&quot;">​</a></h1><p>The Virginia Institute of Marine Science shall hereafter be referred to as the Institute. The Institute shall:</p><ol><li><p>Conduct studies and investigations of the seafood and commercial fishing and sport fishing industries;</p></li><li><p>Consider ways to conserve, develop and replenish fisheries resources and advise the Marine Resources Commission and other agencies and private groups on these matters;</p></li><li><p>Conduct studies of problems pertaining to the other segments of the maritime economy;</p></li><li><p>Conduct studies of marine pollution in cooperation with the State Water Control Board and the Department of Health and make the data and their recommendations available to the appropriate agencies;</p></li><li><p>Conduct hydrographic and biological studies of the Chesapeake Bay, its tributaries, and all the tidal waters of the Commonwealth and the contiguous waters of the Atlantic Ocean;</p></li><li><p>Engage in research in the marine sciences;</p></li><li><p>Conduct such special studies and investigations concerning these subjects as requested by the Governor;</p></li><li><p>Engage in research and provide training, technical assistance and advice to the Board of Conservation and Recreation on erosion along tidal shorelines, the Soil and Water Conservation Board on matters relating to tidal shoreline erosion, and to other agencies upon request; and</p></li><li><p>Develop comprehensive coastal resource management guidance for local governments to foster the sustainability of shoreline resources by December 30, 2012. The guidance shall identify preferred options for shoreline management and taking into consideration the resource condition, priority planning, and forecasting of the condition of the Commonwealth&#39;s shoreline with respect to projected sea-level rise.</p></li></ol><p>These studies shall include consideration of the seafood and other marine resources, such as the waters, bottoms, shorelines, tidal wetlands, and beaches, and all matters related to marine waters and the means by which marine resources might be conserved, developed and replenished.</p><p>Code 1950, §§ 28-248, 28-250, 28-250.1; 1962, c. 406, § 28.1-195; 1979, c. 294; 1980, c. 369; 1992, c. 836; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>.</p>',5),r=[o];function s(c,d,l,h,p,u){return i(),t("div",null,r)}const m=e(a,[["render",s]]);export{f as __pageData,m as default};
