import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 28.2-1205.1 Coordinated review of water resources projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-1205.1.md","filePath":"vacode/28.2-1205.1.md"}'),r={name:"vacode/28.2-1205.1.md"},o=i('<h1 id="§-28-2-1205-1-coordinated-review-of-water-resources-projects" tabindex="-1">§ 28.2-1205.1 Coordinated review of water resources projects. <a class="header-anchor" href="#§-28-2-1205-1-coordinated-review-of-water-resources-projects" aria-label="Permalink to &quot;§ 28.2-1205.1 Coordinated review of water resources projects.&quot;">​</a></h1><p>A. Applications for water resources projects that require a Virginia Marine Resources permit and an individual Virginia Water Protection Permit under § <a href="/vacode/62.1-44.15:20/">62.1-44.15:20</a> shall be submitted and processed through a joint application and review process.</p><p>B. The Commissioner and the Director of the Department of Environmental Quality, in consultation with the Virginia Institute of Marine Science, the Department of Wildlife Resources, the Department of Historic Resources, the Department of Health, the Department of Conservation and Recreation, the Virginia Department of Agriculture and Consumer Services, and any other appropriate or interested state agency, shall coordinate the joint review process to ensure the orderly evaluation of projects requiring both permits.</p><p>C. The joint review process shall include, but not be limited to, provisions to ensure that: (i) the initial application for the project shall be advertised simultaneously by the Commission and the Department of Environmental Quality; (ii) project reviews shall be completed by all state agencies that have been asked to review and provide comments, within 45 days of project notification by the Commission and the Department of Environmental Quality; (iii) the Commission and the Department of Environmental Quality shall coordinate permit issuance and, to the extent practicable, shall take action on the permit application no later than one year after the agencies have received complete applications; (iv) to the extent practicable, the Commission and the Department of Environmental Quality shall take action concurrently, but no more than six months apart; and (v) upon taking its final action on each permit, the Commission and the Department of Environmental Quality shall provide each other with notification of its action and any and all supporting information, including any background materials or exhibits used in the application.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0049" target="_blank" rel="noreferrer">49</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0356" target="_blank" rel="noreferrer">356</a>.</p>',5),n=[o];function s(c,l,p,h,d,m){return t(),a("div",null,n)}const v=e(r,[["render",s]]);export{f as __pageData,v as default};
