import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 28.2-1406 Permits required for certain activities; issuance of permits by Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-1406.md","filePath":"vacode/28.2-1406.md"}'),o={name:"vacode/28.2-1406.md"},a=t('<h1 id="§-28-2-1406-permits-required-for-certain-activities-issuance-of-permits-by-commission" tabindex="-1">§ 28.2-1406 Permits required for certain activities; issuance of permits by Commission. <a class="header-anchor" href="#§-28-2-1406-permits-required-for-certain-activities-issuance-of-permits-by-commission" aria-label="Permalink to &quot;§ 28.2-1406 Permits required for certain activities; issuance of permits by Commission.&quot;">​</a></h1><p>A. It shall be unlawful for any person to conduct any activity which would require a permit under a coastal primary sand dune zoning ordinance without such a permit. Until the county, city or town in which a person proposes to conduct an activity which would require a permit under the ordinance adopts the ordinance, such person shall apply for a permit directly to the Commission. Permit applicants desiring to use or develop dunes or beaches owned by the Commonwealth shall also apply directly to the Commission.</p><p>B. The Commission shall process permit applications in accordance with the provisions of the Coastal Primary Sand Dune Zoning Ordinance and the Commissioner, or his authorized representative, shall sign any permit granted; however, the Commission may designate one or more hearing officers who may, in lieu of the Commission, conduct public hearings as required under § <a href="http://law.lis.virginia.gov/vacode/28.2-1403/" target="_blank" rel="noreferrer">28.2-1403</a> and thereafter report their findings and recommendations to the Commission.</p><p>1972, c. 711, § 62.1-13.9; 1980, c. 660, § 62.1-13.26; 1992, c. 836; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0112" target="_blank" rel="noreferrer">112</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0125" target="_blank" rel="noreferrer">125</a>.</p>',4),n=[a];function s(c,m,d,p,h,l){return i(),r("div",null,n)}const _=e(o,[["render",s]]);export{f as __pageData,_ as default};
